SELECT Student.Name, SUM(Equipment.Points) FROM Student INNER JOIN Loan ON Loan.StudentID = Student.ID INNER JOIN Equipment ON Equipment.ID = Loan.EqptID WHERE Student.Name = "Andy" GROUP BY Student.Name UNION ALL SELECT Student.Name, SUM(Equipment.Points) AS s FROM Student INNER JOIN Loan ON Loan.StudentID = Student.ID INNER JOIN Equipment ON Equipment.ID = Loan.EqptID GROUP BY Student.Name ORDER BY s DESC LIMIT 4;