SELECT ExamSession.SubjectName, ExamSession.PaperNo, ExamDuty.Role, Venue.VenueName, ExamSession.`Date`, ExamSession.StartTime, ExamSession.EndTime FROM ExamSession INNER JOIN ExamDuty ON ExamDuty.ExamSessionID = ExamSession.ExamSessionID INNER JOIN Teacher ON Teacher.TeacherID = ExamDuty.TeacherID INNER JOIN Venue ON Venue.VenueID = ExamSession.VenueID WHERE Teacher.Name = "Derren Brett" ORDER BY `Date` ASC, StartTime ASC;;